(_index=sumologic_volume) _sourcecategory={{data_volume_view}}_and_tier_volume
| parse regex "\{\"field\":\"(?<value>[^\"]+)\",\"dataTier\":\"(?<dataTier>[^\"]+)\",\"sizeInBytes\":(?<sizeInBytes>[^\"]+),\"count\":(?<count>[^\"]+)\}" multi

| sum(count) as events,sum(sizeinbytes) as bytes by  value,_sourcecategory // datatier
//| sum(sizeinbytes) as bytes by datatier, value,_timeslice
| bytes /1024/1024/1024 as gb | sort gb
//| parse field=_sourcecategory "*_and_tier_volume" as dimension 


// remove long categories
| if (length(value) > {{value_max_chr}},concat(substring(value,0,{{value_max_chr}}),"..."),value) as value 
| count as categories,sum(events) as events,sum(gb) as gbytes by value //,dataTier

|  sum(gbytes) as gbytes by value //datatier
// compare with previous 3 time periods at same time
| compare timeshift 7d 3 avg

// this handles nulls for new or removed categories
| if(isNull(gbytes), "GONE", "") as state 
| if(isNull(gbytes), 0, gbytes) as gbytes 

| if(isNull(gbytes_21d_avg), 0, gbytes_21d_avg) as gbytes_21d_avg 

// what is the change delta as a percentage vs baseline
| if(gbytes = 0,0,100 * (gbytes/gbytes_21d_avg)) as pct_change

// this line is for your thresholds to get notified
// consider tuning the  min size clause for your environment.
| if (pct_change > 150,"Ingest Spike",state) as state
| if(gbytes_21d_avg = 0, "NEW", state) as state 
| where ( state = "NEW" or state = "Ingest Spike") and ( gbytes > 1)

| sort pct_change

