// This query demonstrates how to work with closed insights to produce a useful table with status, times and operators.

_index=sumologic_audit_events _sourcecategory=cseinsight closed insightupdated
//OR
//_index=sumologic_system_events _sourcecategory=cseinsight insightcreated)



| json field=_raw "eventName"
//| json field=_raw "insightIdentity.id" as id
| json field=_raw "insightIdentity.readableId" as insightid
| json field=_raw "insight.status" as status
| json field=_raw "insight.name" as name
| json field=_raw "insight.entityType" as entitytype
| json field=_raw "insight.entityValue" as entityvalue
//| json field=_raw "insight.tags" as tags
| json field=_raw "insight.severity" as severity
| json field=_raw "insight.confidence" as confidence
| json field=_raw "insight.signals" as signals

// in closed events
| json field=_raw "insight.assignee" as assignee nodrop
| json field=_raw "insight.resolution" as resolution nodrop

// in update events 
//| json field=_raw "to" as last_change nodrop

// may exist after an update
| json field=_raw "insight.timeToResponse" as timeToResponse nodrop
| json field=_raw "insight.timeToDetection" as timeToDetection nodrop
| json field=_raw "insight.timeToRemediation" as timeToRemediation nodrop

| if (isnull(timeToResponse),-1,timeToResponse) as timeToResponse
| if (isnull(timeToDetection),-1,timeToDetection) as timeToDetection
| if (isnull(timeToRemediation),-1,timeToRemediation) as timeToRemediation

//| parse regex field=signals "(?<signal>\{\"id\":\"[^\}]+\})" multi
//| json field=signal "tags" as signal_tags
//| parse regex field=signal_tags "\"(?<tagname>[^:\"]+):(?<tagvalue>[^:\"]+)" multi
////
| json field=_raw "insight.closed" as time_closed nodrop
|substring(time_closed,0,23) as time_closed

| json field=_raw "insight.created" as time_created nodrop
|substring(time_created,0,23) as time_created


| json field=_raw "operator.sourceIp" as op_ip nodrop
| json field=_raw "operator.email" as op_email nodrop
| sort _messagetime
//// ensure we only store the most recent result and remove duplicate exploded events
| count as events, max(_messagetime) as _messagetime,first(status) as status, max(severity) as severity,first(confidence) as confidence,first(assignee) as assignee,first(resolution) as resolution,first(timeToResponse) as timeToResponse,first(timeToDetection) as timeToDetection,first(timeToRemediation) as timeToRemediation, first(op_ip) as closed_ip,first(op_email) as closed_by,first(time_created) as time_created,first(time_closed) as time_closed by insightid,name,entitytype,entityvalue 

| where status = "closed"
| parsedate(time_created,"yyyy-MM-dd'T'HH:mm:ss.SSS") as created_epoc

// timeToResponse = seconds between created time and first status update
// timeToRemediation = seconds between created and closed epoc times in the close event

// this would give you the save number as timeToRemediation already has
// | parsedate(time_closed,"yyyy-MM-dd'T'HH:mm:ss.SSS") as closed_epoc
// | (closed_epoc - created_epoc) / 1000 as duration_s

// set your time you want to measure start and end shift times here
| "Pacific/Auckland" as shift_TZ
| formatDate(toLong(created_epoc), "yyyy-MM-dd HH:mm:ss ZZZ", shift_TZ) as created_local_time
| formatDate(toLong(created_epoc ), "E", shift_TZ) as created_day
| formatDate(toLong(created_epoc ), "HH", shift_TZ) as created_hour

| formatDate(toLong(created_epoc + timeToRemediation), "yyyy-MM-dd'T'HH:mm:ss.SSS ZZZ", shift_TZ) as closed_local_time
| formatDate(toLong(created_epoc + timeToRemediation), "E", shift_TZ) as closed_day
| formatDate(toLong(created_epoc + timeToRemediation), "HH", shift_TZ) as closed_hour

// enter shift rules here 
| if(created_day matches /Sat|Sun/,"weekend","weekday") as weekday
| if(weekday = "weekday" and (num(created_hour) >= 7 and num(created_hour) < 19),"inside","outside") as working_hours

// from here you could report on them as bands for example:
| timeToRemediation - timetoresponse as timeToRemediation

// reporting in minutes is easier for humans
| timeToDetection / 60 as timeToDetection
| timeToResponse / 60 as timeToResponse
| timeToRemediation / 60 as timeToRemediation
| avg(timeToDetection) as timeToDetection, avg(timeToResponse) as timeToResponse, avg(timeToRemediation) as timeToRemediation by insightid,working_hours,closed_by,created_local_time,resolution,severity,confidence,created_day,created_hour | sort timetoRemediation

