cat {{{lookup}}}
| where createdtime > (now () - (1000 * 60 * 60 * 24 * {{days}}))
| tolong(createdtime) as _messagetime
| timeslice by 1w 
//| timeToDetection / 3600 as timeToDetection

| count as insights, pct(timetoresponse,50) as MTTResponse , pct(timetoremediation,50) as MTTResolution by _timeslice

