queries.queryString:
_sourceCategory=*events  cluster={{cluster}} 
not "\"type\":\"Normal\""
// exclude any annoying messages here
involvedObject
not "Nameserver limits were exceeded"
not "Liveness probe failed"
| json field=_raw "object.reason" as reason
//| json field=_raw "object.involvedObject.kind" as type
//| json field=_raw "object.involvedObject.name" as name
| json field=_raw "object.type" as level
//| json field=_raw "object.message" as message
//| json field=_raw "object.metadata.namespace" as namespace

| concat(level,"-",reason) as event
| timeslice | count by _timeslice,event | transpose row _timeslice column event

((#B/#A) * 100) along cluster, node 
| topk({{top_n}},max)
//| where _value > 80

 metric=container_cpu_usage_seconds_total cluster={{cluster}} pod=* 
// quantize by max not avg 
//| quantize using  rate | topk(50,max) 
| delta counter | topk({{top_n}},sum) 
//| rate increasing over 1m | topk(50,max) 
| sum by pod,container //| avg by cluster, pod | where _value > 800

 metric=container_cpu_usage_seconds_total cluster={{cluster}} pod=* container=*
// quantize by max not avg 
//| quantize using  rate | topk(50,max) 
| delta counter | topk({{top_n}},sum) 
//| rate increasing over 1m | topk(50,max) 
| sum by cluster, pod, container |   eval (_value * 100)   //| avg by cluster, pod | where _value > 800
