_view=metadata_*
sourcecategory = {{sourcecategory}}
collector = {{collector}}
index = {{index}}
sourcehost = {{sourcehost}}
| where ("{{global_regex_search}}" = ".*") or (concat(collector,index,source,sourcecategory,sourcehost) matches /(?i){{global_regex_search}}/)
| concat(index," ",sourcecategory) as index_category
| concat("_index=",index," _sourcecategory=",sourcecategory ) as %"Search Scope"
| sum(bytes) as bytes, sum(events) as events, first(collector) as collector, first(source) as source, first(sourcehost) as sourcehost,count_distinct(collector) as collectors, count_distinct(source) as sources, count_distinct(sourcehost) as hosts by index,sourcecategory,%"Search Scope", tier  | round(bytes / 1G,3) as gb
| total events as te | 100 * ( events/te) as percent | fields -te

// this might change per sumo instance!  
| "https://{{my_instance_name}}.sumologic.com/ui/#/search/create?query=" as dp

// prepare the category strings
| concat(" _sourcecategory=",replace(sourcecategory," ","?")) as sc
| concat("\n _index=",replace(index," ","?")) as i
| concat("\n// _collector=",replace(collector," ","?")) as c
| concat("\n// _source=",replace(source," ","?")) as s
| concat("\n// _sourcehost=",replace(sourcehost," ","?")) as sh
| urlencode(concat(i,sc,c,s,sh)) as the_search

// make final url columns
| tourl(concat(dp,the_search,"&startTime=-1h"),"Search") as  %"Search"

| fields -dp,sc,i,c,sh,s,the_search, collector,source,bytes,sourcehost
| sort index asc,sourcecategory asc | fields -percent

