queries.queryString:
_view=sumologic_search_usage_per_query {{{filter}}}
| timeslice by {{timeslice}}
| execution_duration_ms / 1000 as execution_duration_s
| pct(execution_duration_s,50) by query_type,_timeslice |transpose row _timeslice column query_type

_view=sumologic_search_usage_per_query {{{filter}}}
//| where execution_duration_ms > 30000
| (query_end_time - query_start_time)/1000 as range_s
| execution_duration_ms /1000 as duration_s
| if(execution_duration_ms > 1000 * {{slow_seconds}},1,0) as slow
| if(status_message = "Finished successfully",0,1) as fail
// AGGREGATE TO GET STATS
| count_distinct(user_name) as users,count as queries,sum(duration_s) as total_sec,count as searches, sum(data_scanned_bytes) as bytes_scanned, sum(retrieved_message_count) as events, avg(duration_s) as avg_sec , sum(slow) as %"slow > 30s", sum(fail) as %"fail/cancelled" by query_type,analytics_tier
| sort bytes_scanned
| bytes_scanned / 1024/1024/1024 as scan_gb | fields -bytes_scanned

_view=sumologic_search_usage_per_query {{{filter}}}
//( query_type=*view*)

| if(execution_duration_ms < 1000 * {{slow_seconds}},1,0) as fast
| sum(fast) as fast

_view=sumologic_search_usage_per_query {{{filter}}}
//( query_type=*view*)

| if(execution_duration_ms < 1000 * {{slow_seconds}},0,1) as slow
| sum(slow) as slow

_view=sumologic_search_usage_per_query {{{filter}}}
| where execution_duration_ms > (1000 * {{slow_seconds}})
| timeslice by {{timeslice}}
|if(execution_duration_ms < 1000 * {{slow_seconds}},0,1) as slow
| sum(slow) as slow_searches by _timeslice,user_name | transpose row _timeslice column user_name

_view=sumologic_search_usage_per_query {{{filter}}}
| where execution_duration_ms > (1000 * {{slow_seconds}})
| timeslice by {{timeslice}}
|if(execution_duration_ms < 1000 * {{slow_seconds}},0,1) as slow
| sum(slow) as slow_searches by _timeslice,query_type | transpose row _timeslice column query_type

_view=sumologic_search_usage_per_query {{{filter}}}
| timeslice by {{timeslice}}

| count by query_type,_timeslice | transpose row _timeslice column query_type

_view=sumologic_search_usage_per_query {{{filter}}}
| timeslice by {{timeslice}}
|if(execution_duration_ms < 1000 * {{slow_seconds}},0,1) as slow
| sum(slow) as slow, count as all by _timeslice | all-slow as fast | fields _timeslice,slow,fast
